@Transient
public int getFrontNineDistance() {
    int distance = 0;
    for (Tee tee : tees) {
        if (tee.getHole().getNumber() <= 9) {
            distance += tee.getDistance();
        }
    }
    return distance;
}

@Transient
public int getBackNineDistance() {
    int distance = 0;
    for ( Tee tee : tees ) {
        if ( tee.getHole().getNumber() > 9 ) {
            distance += tee.getDistance();
        }
    }
    return distance;
}
